@AccessControl.authorizationCheck: #NOT_REQUIRED
@EndUserText.label: 'Company Master Data'
@Metadata.ignorePropagatedAnnotations: true
define root view entity zetr_ddl_i_company_master_data
  as select from    I_CompanyCode as CompanyCode
    left outer join zetr_t_cmpin  as CompanyMaster   on CompanyMaster.bukrs = CompanyCode.CompanyCode
    left outer join zetr_t_eipar  as eInvoiceMaster  on eInvoiceMaster.bukrs = CompanyCode.CompanyCode
    left outer join zetr_t_eapar  as eArchiveMaster  on eArchiveMaster.bukrs = CompanyCode.CompanyCode
    left outer join zetr_t_edpar  as eDeliveryMaster on eDeliveryMaster.bukrs = CompanyCode.CompanyCode
    left outer join zetr_t_arcd   as Archive         on  Archive.docui = CompanyMaster.docui
                                                     and Archive.conty = 'UBL'
                                                     and Archive.docty = 'MASTER'
{
  key CompanyCode.CompanyCode                  as CompanyCode,
      CompanyCode.CompanyCodeName              as CompanyCodeName,
      CompanyCode.AddressID                    as AddressID,

      cast( case eInvoiceMaster.bukrs
              when CompanyCode.CompanyCode then 'X'
              else '' end as abap_boolean )    as eInvoiceDone,
      case eInvoiceMaster.bukrs
        when CompanyCode.CompanyCode then 3
        else 2 end                             as eInvoiceCriticality,
      cast( case eArchiveMaster.bukrs
              when CompanyCode.CompanyCode then 'X'
              else '' end as abap_boolean )    as eArchiveDone,
      case eArchiveMaster.bukrs
        when CompanyCode.CompanyCode then 3
        else 2 end                             as eArchiveCriticality,
      cast( case eDeliveryMaster.bukrs
              when CompanyCode.CompanyCode then 'X'
              else '' end as abap_boolean )    as eDeliveryDone,
      case eDeliveryMaster.bukrs
        when CompanyCode.CompanyCode then 3
        else 2 end                             as eDeliveryCriticality

//      Archive.contn                            as Content,
//      cast( case Archive.conty
//              when 'PDF' then 'application/pdf'
//              when 'UBL' then 'text/xml'
//              when 'HTML' then 'text/html'
//              else '' end as zetr_e_mimet )    as MimeType,
//      cast( case Archive.conty
//              when 'PDF' then concat( CompanyCode.CompanyCodeName, '.pdf' )
//              when 'UBL' then concat( CompanyCode.CompanyCodeName, '.xml' )
//              when 'HTML' then concat( CompanyCode.CompanyCodeName, '.html' )
//              else '' end as zetr_e_filename ) as Filename
}
where
  CompanyCode.Country = 'TR'

union all

select from       I_Country   as Country
  left outer join zetr_t_arcd as Archive on Archive.docty = Country.Country
{
  key '*GIB'            as CompanyCode,
      'GÄ°B Master Data' as CompanyCodeName,
      ''                as AddressID,
      ''                as eInvoiceDone,
      0                 as eInvoiceCriticality,
      ''                as eArchiveDone,
      0                 as eArchiveCriticality,
      ''                as eDeliveryDone,
      0                 as eDeliveryCriticality
//      Archive.contn     as Content,
//      ''                as MimeType,
//      ''                as Filename
}
where
  Country.Country = 'TR'
