@AccessControl.authorizationCheck: #NOT_REQUIRED
@EndUserText.label: 'eInvoice Public IBAN Mapping'
@Metadata.ignorePropagatedAnnotations: true
define root view entity zetr_ddl_i_public_iban_mapping
  as select from    zetr_t_eipi                                                    as Main
    left outer join DDCDS_CUSTOMER_DOMAIN_VALUE_T( p_domain_name: 'ZETR_D_EIPMC' ) as PaymentMethod on  PaymentMethod.language  = $session.system_language
                                                                                                    and PaymentMethod.value_low = Main.payment
    left outer join I_Customer                                                     as Customer      on Customer.Customer = Main.kunnr
  //composition of target_data_source_name as _association_name
{
  key Main.taxid            as TaxID,
      Main.kunnr            as Customer,
      Customer.CustomerName as CustomerName,
      Main.iban             as IBAN,
      Main.payment          as PaymentMethod,
      PaymentMethod.text    as PaymentMethodText
      //    _association_name // Make association public
}
